sqlinfo.sql:
		SELECT
			t1.id,
			t1.jy_material_id,
			m1.`code` material_code,
			m1.`name` material_name,
			m1.model,
			m1.supplier,
			t1.use_user,
			u1.`name` user_name,
			t1.use_date,
			t1.use_number,
			t1.remark,
			t1.`status`
		FROM
			jy_material_use t1
		LEFT JOIN jy_user u1 ON t1.use_user = u1.id
		LEFT JOIN jy_material m1 ON t1.jy_material_id = m1.id
		WHERE
			(
				t1.use_user = $N{sys_user_id}
			)
			AND
			(
                $ISNULL{id}
                OR t1.id = $N{id}
            ) 
			AND
			(
                $ISNULL{jy_material_id}
                OR t1.jy_material_id = $N{jy_material_id}
            ) 
            AND
			(
                $ISNULL{status}
                OR t1.status = $N{status}
            ) 
            AND
			(
               $ISNULL{date_start}
               or 
               t1.use_date >= $S{date_start}
            )
             AND
            (
                $ISNULL{date_end}
                or
                t1.use_date <= $S{date_end}
            )

		SELECT
			t1.id,
			t1.jy_material_id,
			m1.`code` material_code,
			m1.`name` material_name,
			m1.model,
			m1.supplier,
			t1.use_user,
			u1.`name` user_name,
			t1.use_date,
			t1.use_number,
			t1.remark,
			t1.`status`
		FROM
			jy_material_use t1
		LEFT JOIN jy_user u1 ON t1.use_user = u1.id
		LEFT JOIN jy_material m1 ON t1.jy_material_id = m1.id
		WHERE

			(
                $ISNULL{id}
                OR t1.id = $N{id}
            ) 
			AND
			(
                $ISNULL{jy_material_id}
                OR t1.jy_material_id = $N{jy_material_id}
            ) 
            AND
			(
                $ISNULL{status}
                OR t1.status = $N{status}
            ) 
            AND
			(
               $ISNULL{date_start}
               or 
               t1.use_date >= $S{date_start}
            )
             AND
            (
                $ISNULL{date_end}
                or
                t1.use_date <= $S{date_end}
            )
            AND
            (
				$ISNULL{use_user_name}
                OR u1.`name` like '%$U{use_user_name}%'
			)
